mapper.insert:
INSERT INTO TB_BOOKDETAIL (
    USER_ID
    , USER_NAME
    , BOOK_TITLE
    , BOOK_AUTHOR
    , BOOK_PUBLISHER
    , BOOK_PRICE
    , BOOK_IMG
    , BOOK_CONTENT
    , BOOK_SELLING_PRICE
) VALUES (
    #{userId}
    , #{userName}
    , #{title}
    , #{author}
    , #{publisher}
    , #{bookPrice}
    , #{bookImg}
    , #{content}
    , #{price}
)

mapper.update:
UPDATE
    TB_BOOKDETAIL
SET
    BOOK_SELLING_PRICE = #{bookPrice}
WHERE BOOK_IDX = #{bookIdx} AND USER_ID = #{userId}

UPDATE
    TB_BOOKDETAIL
SET
    BOOK_CONTENT = #{bookContent}
WHERE BOOK_IDX = #{bookIdx} AND USER_ID = #{userId}

mapper.insert:
